    function movePiece(x, y) {
        let squares = boardSquare;

        let targetSquare = getATargetSquareByLocation(x, y)
        let square = squares.find(s => s.x == selectedSquareX && s.y == selectedSquareY)

        let color = getPieceColor(square)
        let piece = getPiece(square)

        //disable castling if the king moves
        if (piece == "king") {
            if (isColorWhite(color)) {
                setCanWhiteLongCastle(false)
                setCanWhiteShortCastle(false)
            } else {
                setCanBlackLongCastle(false)
                setCanBlackShortCastle(false)
            }
        }

        //disable castling if the rook moves
        if (piece == "rook") {
            if (square.x == 7 && square.y == 7) {
                setCanWhiteShortCastle(false)
            }

            if (square.x == 0 && square.y == 7) {
                setCanBlackShortCastle(false)
            }

            if (square.x == 7 && square.y == 0) {
                setCanWhiteLongCastle(false)
            }

            if (square.x == 0 && square.y == 0) {
                setCanBlackLongCastle(false)
            }
        }

        let p = UpdatePosition(square, targetSquare, position)

        //capturing enPassant
        if (enPassantX && enPassantY && enPassantPossible) {
            let enPassantTargetSquare = color == "white" ? squares.find(s => s.x == x + 1 && s.y == y) : squares.find(s => s.x == x - 1 && s.y == y)
            let enPassantSquare = <Square x={square.x} y={square.y} piece={""} />

            p = UpdatePosition(enPassantSquare, enPassantTargetSquare, p)
        }

        //TODO: ensure p matches the new board Position format
        //setBoardPosition(p)
        setPossibleMoves([])
        setIsWhiteToMove(!isWhiteToMove)

        //enabling en passant
        if (piece == "pawn") {
            if (Math.abs(square.x - targetSquare.x) == 2) {
                let enPassant1 = squares.find(s => s.x == targetSquare.x && s.y == targetSquare.y - 1)
                let enPassant2 = squares.find(s => s.x == targetSquare.x && s.y == targetSquare.y + 1)

                if (enPassant1) {
                    if (enPassant1.piece.split("-")[1] == "pawn") {
                        if (enPassant1.piece.split("-")[0] != color) {
                            setEnPassantPossible(true)
                            setEnPassantX(targetSquare.x)
                            setEnPassantY(targetSquare.y)
                            return
                        }
                    }
                }

                if (enPassant2) {
                    if (enPassant2.piece.split("-")[1] == "pawn") {
                        if (enPassant2.piece.split("-")[0] != color) {
                            setEnPassantPossible(true)
                            setEnPassantX(targetSquare.x)
                            setEnPassantY(targetSquare.y)
                            return
                        }
                    }
                }
            }
        }

        setEnPassantPossible(false)
        setEnPassantX('')
        setEnPassantY('')
    }

    // //updates the state with the possible moves
    function updatePossibleMoves(moves) {
        setPossibleMoves(moves)
    };

    // //updates the state with the selectedSquare X coordinates
    function updateSelectedSquareX(x) {
        setSelectedSquareX(x)
    }

    // //updates the state with the selectedSquare Y coordinates
    function updateSelectedSquareY(y) {
        setSelectedSquareY(y)
    }

    // //retrieves a speecific square from the board using x and y coordinates (location)
    function getATargetSquareByLocation(x, y) {
        let squares = boardSquare

        return squares.find(s => s.x === x && s.y === y);
    }

