import React, { useState, useEffect } from 'react';
import { CurrentSquareSelection, PossibleMoves } from "./globalVariables.js"

function Square({
    getPossibleMoves,
    x,
    y,
    piece,
    color,
    updatePossibleMoves,
    movePiece
}) {

    const [styling, setStyling] = useState(['chessSquare', 'piece', color, piece])

    function handleClick() {
        console.log(`Square object`, PossibleMoves)
        if (PossibleMoves.some(m => m.piece.props.x == x && m.piece.props.y == y)) {
            movePiece(x, y)
        } else {
            if (CurrentSquareSelection.x == x && CurrentSquareSelection.y == y) {
                CurrentSquareSelection.x = -1
                CurrentSquareSelection.y = -1
                PossibleMoves.splice(0, PossibleMoves.length);
            } else {
                CurrentSquareSelection.x = x
                CurrentSquareSelection.y = y
                getPossibleMoves()
                if (PossibleMoves.some(move => move.moves.some(m => m.props.x === x && m.props.y === y))) {
                    styles.push("possibleMove");
                    setStyling(prevStyling => [...prevItems, newItem])
                }
            }
        }
    }

    // let styles = ['chessSquare', 'piece'];
    // styles.push(color);
    // styles.push(piece);

    if (PossibleMoves.some(move => move.moves.some(m => m.props.x === x && m.props.y === y))) {
        styles.push("possibleMove");
    }

    return (
        <>
            <div onClick={handleClick} className={styles.filter(Boolean).join(" ")}>
                {/* <span className='coordinates'>{x}-{y}</span> */}
            </div>
        </>
    )
}

export default Square
