import React, { useState, useEffect } from 'react';

function Square({ getPossibleMoves, x, y, piece, color, selectedSquareX, setSelectedSquareX, selectedSquareY, setSelectedSquareY, possibleMoves, updatePossibleMoves, movePiece }) {
    useEffect(() => {
        console.log('Update from Square', this.state.possibleMoves)
    }, [])

    onClick = () => {
        console.log(this.props.possibleMoves.some(m => m.piece.props.x == this.props.x && m.piece.props.y == this.props.y))
        console.log(this.props.possibleMoves)
        if (this.props.possibleMoves.some(m => m.piece.props.x == this.props.x && m.piece.props.y == this.props.y)) {
            console.log('Moving')
            this.props.movePiece(this.props.x, this.props.y)
        } else {
            if (this.props.selectedSquareX == this.props.x && this.props.selectedSquareY == this.props.y) {
                this.props.setSelectedSquareX("")
                this.props.setSelectedSquareY("")
                this.props.updatePossibleMoves([])
            } else {
                this.props.setSelectedSquareX(this.props.x)
                this.props.setSelectedSquareY(this.props.y)
                this.props.getPossibleMoves(this)
            }
        }
    }

    let styles = ['chessSquare', 'piece'];
    styles.push(this.props.color);
    styles.push(this.props.piece);

    if (this.props.possibleMoves.some(move => move.x === this.props.x && move.y === this.props.y)) {
        styles.push("possibleMove");
    }

    return
    <>
        <div onClick={this.onClick} className={styles.filter(Boolean).join(" ")}></div>
    </>
}
