import React, { useState, useEffect } from 'react';

function Square({ getPossibleMoves, x, y, piece, color, selectedSquareX, setSelectedSquareX, selectedSquareY, setSelectedSquareY, possibleMoves, updatePossibleMoves, movePiece }) {
    useEffect(() => {
        console.log('Update from Square', this.state.possibleMoves)
    }, [])

    function handleClick() {
        console.log(possibleMoves.some(m => m.piece.props.x == this.props.x && m.piece.props.y == this.props.y))
        console.log(possibleMoves)
        if (possibleMoves.some(m => m.piece.props.x == this.props.x && m.piece.props.y == this.props.y)) {
            console.log('Moving')
            movePiece(x, this.props.y)
        } else {
            if (selectedSquareX == x && selectedSquareY == y) {
                setSelectedSquareX("")
                setSelectedSquareY("")
                updatePossibleMoves([])
            } else {
                setSelectedSquareX(x)
                setSelectedSquareY(y)
                getPossibleMoves(this)
            }
        }
    }

    let styles = ['chessSquare', 'piece'];
    styles.push(color);
    styles.push(piece);

    if (possibleMoves.some(move => move.x === x && move.y === y)) {
        styles.push("possibleMove");
    }

    return (
        <>
            <div onClick={handleClick} className={styles.filter(Boolean).join(" ")}></div>
        </>
    )
}
