import {
    PIECES, FEN_PIECES_BLACK, COLORS, CASTLE_ROW_WHITE, CASTLE_ROW_BLACK, CASTLE_KING_INITIAL_COL, CASTLE_ROOK_INITIAL_COL_LONG, CASTLE_ROOK_INITIAL_COL_SHORT,
    CASTLE_PATH_COLS_LONG, CASTLE_PATH_COLS_SHORT, PIECE_COLOR_INDEX, PIECE_PIECE_INDEX, FEN_PIECES_WHITE, EMPTY_SQUARE_PIECE, CASTLE_ROOK_FINAL_COL_LONG,
    CASTLE_ROOK_FINAL_COL_SHORT, CASTLE_KING_FINAL_COL_LONG, CASTLE_KING_FINAL_COL_SHORT, EMPTY_SQUARES_AFTER_LONG_CASTLE, EMPTY_SQUARES_AFTER_SHORT_CASTLE,
    BoardPosition, PIECE_DELIMITER, CASTLING_TYPES
} from './globalVariables'

const FEN_PIECE_MAP = {
    [FEN_PIECES_BLACK.PAWN]: PIECES.PAWN,
    [FEN_PIECES_BLACK.KNIGHT]: PIECES.KNIGHT,
    [FEN_PIECES_BLACK.KING]: PIECES.KING,
    [FEN_PIECES_BLACK.QUEEN]: PIECES.QUEEN,
    [FEN_PIECES_BLACK.ROOK]: PIECES.ROOK,
    [FEN_PIECES_BLACK.BISHOP]: PIECES.BISHOP,
};

//Checks if a character is Uppercase using the ASCII table; 65 (A); 90 (Z)
export function IsUpperCase(value) {
    return value.charCodeAt(0) >= 65 && value.charCodeAt(0) <= 90
}

//Checks if a character is Lowercase using the ASCII table; 97 (a); 122 (z)
export function IsLowerCase(value) {
    return value.charCodeAt(0) >= 97 && value.charCodeAt(0) <= 122
}

//Checks if a character is a Digit using the ASCII table; 48 (0); 57 (9)
export function IsDigit(value) {
    return value.charCodeAt(0) >= 48 && value.charCodeAt(0) <= 57
}

//splits the FEN string to each individual part that makes it
export function ParseFEN(fen) {
    const fenParts = fen.split(' ');

    if (fenParts.length != 6) {
        console.log('Invalid FEN')
        return;
    }

    const fenBoard = fenParts[0];
    const fenTurn = fenParts[1];
    const fenCastling = fenParts[2];
    const fenEnPassant = fenParts[3];
    const fenHalfMoves = Number(fenParts[4])
    const fenFullMoves = Number(fenParts[5])

    return { fenBoard, fenTurn, fenCastling, fenEnPassant, fenHalfMoves, fenFullMoves }
}

//converts FEN piece notation to an actual piece
export function ConvertFENtoPiece(letter) {
    return FEN_PIECE_MAP[letter.toLowerCase()] || "";
}

//converts the FEN piece notation to a piece that we can use in the square component
export function ConvertFENPieceToPiece(value) {
    if (IsUpperCase(value)) {
        return `${COLORS.WHITE}-${ConvertFENtoPiece(value)}`;
    }

    if (IsLowerCase(value)) {
        return `${COLORS.BLACK}-${ConvertFENtoPiece(value)}`;
    }

    return "";
}

export function ConvertFenToString(value) {
    let result = [];

    for (let i = 0; i < value.length; i++) {
        if (IsDigit(value.charAt(i))) {
            for (let j = 0; j < Number(value.charAt(i)); j++) {
                result.push(EMPTY_SQUARE_PIECE)
            }
        } else {
            result.push(value.charAt(i))
        }
    }

    return result.join('');
}

function checkIfTwoSquaresAreOnTheSameRow(square1, square2) {
    return square1.props.x == square2.props.x
}

function isSquareOnRow(square, rowNumber) {
    return square.props.x == rowNumber
}

//gets the piece
export function getPiece(square) {
    return square.props.piece.split(PIECE_DELIMITER)[PIECE_PIECE_INDEX]
}

//checks if piece is positioned on a certain square
export function doesTheSquareHasThePiece(square, piece) {
    let p = getPiece(square)

    return p == piece
}

//gets the piece color
export function getPieceColor(square) {
    return square.props.piece.split(PIECE_DELIMITER)[PIECE_COLOR_INDEX]
}

//checks if a color is white
export function isColorWhite(color) {
    return color == COLORS.WHITE
}

function areWeCastling(square1, square2) {
    return Math.abs(square1.props.y - square2.props.y) == 2
}

function areWeShortCastling(castlingType, withWhite = true) {
    const piecesCastling = withWhite ? CASTLE_ROW_WHITE : CASTLE_ROW_BLACK
    const pieces = withWhite ? [...FEN_PIECES_WHITE] : [...FEN_PIECES_BLACK]
    const [rookCol, kingCol, pathCols] = castlingType === CASTLING_TYPES.LONG ?
        [CASTLE_ROOK_INITIAL_COL_LONG, CASTLE_KING_INITIAL_COL, CASTLE_PATH_COLS_LONG] :
        [CASTLE_ROOK_INITIAL_COL_SHORT, CASTLE_KING_INITIAL_COL, CASTLE_PATH_COLS_SHORT];


    return BoardPosition[piecesCastling][rookCol] == pieces.ROOK &&
        BoardPosition[piecesCastling][kingCol] == pieces.KING &&
        pathCols.every(col => BoardPosition[piecesCastling][col] == EMPTY_SQUARE_PIECE);
}

function areWeLongCastling(withWhite = true) {
    const piecesCastling = withWhite ? CASTLE_ROW_WHITE : CASTLE_ROW_BLACK
    const pieces = withWhite ? [...FEN_PIECES_WHITE] : [...FEN_PIECES_BLACK]

    return BoardPosition[piecesCastling][CASTLE_ROOK_INITIAL_COL_LONG] == pieces.ROOK &&
        BoardPosition[piecesCastling][CASTLE_KING_INITIAL_COL] == pieces.KING &&
        CASTLE_PATH_COLS_LONG.every(col => BoardPosition[piecesCastling][col] == EMPTY_SQUARE_PIECE);
}

function updateColumnOnARow(row, colIndex, newValue) {
    row[colIndex] = newValue
    return row
}

function handleSquareOnCurrentRow(square, targetSquare, row) {
    if (checkIfTwoSquaresAreOnTheSameRow(square, targetSquare)) {
        return handleSquareAndTargetSquareOnTheSameRow(square, targetSquare, row);
    }

    return updateColumnOnARow(row, square.props.y, EMPTY_SQUARE_PIECE)
}

function handleTargetSquareOnCurrentRow(square, targetSquare, row) {
    return updateColumnOnARow(row, targetSquare.props.y, ConvertPieceToFENPiece(square.props.piece))
}

function handleSquareAndTargetSquareOnTheSameRow(square, targetSquare, row) {
    if (doesTheSquareHasThePiece(square, PIECES.KING) && areWeCastling(square, targetSquare)) {
        row = handleCastling(square, row)
    }

    return SidewaysCapture(row, Number(square.props.y), Number(targetSquare.props.y), square.props.piece)
}

function handleCastling(square, row) {
    const color = getPieceColor(square)
    const isWhite = isColorWhite(color);
    const kingPiece = isWhite ? FEN_PIECES_WHITE.KING : FEN_PIECES_BLACK.KING;
    const rookPiece = isWhite ? FEN_PIECES_WHITE.ROOK : FEN_PIECES_BLACK.ROOK;

    if (areWeLongCastling() || areWeLongCastling(false)) {
        EMPTY_SQUARES_AFTER_LONG_CASTLE.forEach(col => row[col] = EMPTY_SQUARE_PIECE);
        row[CASTLE_KING_FINAL_COL_LONG] = kingPiece
        row[CASTLE_ROOK_FINAL_COL_LONG] = rookPiece
    }

    if (areWeShortCastling() || areWeShortCastling(false)) {
        EMPTY_SQUARES_AFTER_SHORT_CASTLE.forEach(col => row[col] = EMPTY_SQUARE_PIECE);
        row[CASTLE_KING_FINAL_COL_SHORT] = kingPiece
        row[CASTLE_ROOK_FINAL_COL_SHORT] = rookPiece
    }
}

export function UpdatePosition(square, targetSquare, position) {
    return position.map((row, rowIndex) => {
        if (isSquareOnRow(square, rowIndex)) {
            row = handleSquareOnCurrentRow(square, targetSquare, row);
        }
        if (isSquareOnRow(targetSquare, rowIndex)) {
            row = handleTargetSquareOnCurrentRow(square, targetSquare, row);
        }
        return row;
    });
}

function SidewaysCapture(row, colIndex, targetColIndex, value) {
    let result = []

    for (let i = 0; i < row.length; i++) {
        if (i == colIndex) {
            result.push(EMPTY_SQUARE_PIECE)
        } else if (i == targetColIndex) {
            result.push(ConvertPieceToFENPiece(value))
        } else {
            result.push(row[i])
        }
    }

    return result
}

function ConvertPieceToFENPiece(value) {
    const tokens = value.split(PIECE_DELIMITER)
    const color = tokens[0]
    const piece = tokens[1]

    let p;

    switch (piece) {
        case PIECES.PAWN:
            p = FEN_PIECES_BLACK.PAWN;
            break;
        case PIECES.KNIGHT:
            p = FEN_PIECES_BLACK.KNIGHT;
            break;
        case PIECES.BISHOP:
            p = FEN_PIECES_BLACK.BISHOP;
            break;
        case PIECES.ROOK:
            p = FEN_PIECES_BLACK.ROOK;
            break;
        case PIECES.QUEEN:
            p = FEN_PIECES_BLACK.QUEEN;
            break;
        case PIECES.KING:
            p = FEN_PIECES_BLACK.KING;
            break;
        default:
            break;
    }

    return isColorWhite(color) ? p.toString().toUpperCase() : p.toString()
}
