const BLACK = 'black';
const PAWN = 'pawn';
const ROOK = 'rook';
const KNIGHT = 'knight';
const BISHOP = 'bishop';
const KING = 'king';
const QUEEN = 'queen';

export function Board() {
    //<summary>
    //FEN - Forsyth-Edwards Notation
    //small letters - black pieces
    //capital letters - white pieces
    //numbers - empty board squares
    //(/) - separates each board row
    //first letter after the board indicates whose turn it is to move (w - white, b - black)
    //next letters indicate castling rights, k - kingside, q - queenside, (-) - no castling allowed for any side
    //en passant square - when a pawn moves two squares, the square behind it is put in this place or (-) if no en passant square
    //the number of half turns (white moves, then black moves = 2 half moves) that were made without a pawn move or a capture of a piece (50-move rule when reaches 100)
    //the number of full moves (white moves, then black moves = 1 full move); increase after every black half move
    //</summary>
    //starting position
    //'rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 0'
    const [fen, setFen] = useState('rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 0')
    const [boardPosition, setBoardPosition] = useState([[]])
    const [nextTurn, setNextTurn] = useState('')
    const [WCastleK, setWCastleK] = useState(true)
    const [WCastleQ, setWCastleQ] = useState(true)
    const [BCastleK, setBCastleK] = useState(true)
    const [BCastleQ, setBCastleQ] = useState(true)
    const [enPassant, setEnPassant] = useState('')
    const [halfMoves, setHalfMoves] = useState(0)
    const [fullMoves, setFullMoves] = useState(0)
    const [boardSquare, setBoardSquare] = useState([])
    const [possibleMoves, setPossibleMoves] = useState([])
    const [selectedSquareX, setSelectedSquareX] = useState('')
    const [selectedSquareY, setSelectedSquareY] = useState('')
    const [canWhiteShortCastle, setCanWhiteShortCastle] = useState(true)
    const [canWhiteLongCastle, setCanWhiteLongCastle] = useState(true)
    const [canBlackShortCastle, setCanBlackShortCastle] = useState(true)
    const [canBlackLongCastle, setCanBlackLongCastle] = useState(true)
    const [enPassantPossible, setEnPassantPossible] = useState(false)
    const [enPassantX, setEnPassantX] = useState('')
    const [enPassantY, setEnPassantY] = useState('')
    const [isWhiteToMove, setIsWhiteToMove] = useState(true)

    //called when the page loads.we need to parse the FEN to fill in the board
    useEffect(() => {
        console.log('Processing FEN')
        fenParser()
    }, [])

    // useEffect(() => {
    //     //console.log(possibleMoves)
    // }, [possibleMoves])

    // useEffect(() => {
    //     console.log('Board position after update', boardPosition)
    // }, [boardPosition])

    useEffect(() => {
        console.log('Board position after update', boardPosition)
    }, [fen, boardPosition, enPassant, boardSquare, possibleMoves,
        selectedSquareX, selectedSquareY, canWhiteShortCastle, canWhiteLongCastle,
        canBlackShortCastle, canBlackLongCastle, enPassantPossible, enPassantX, enPassantY, isWhiteToMove])

    //parses the FEN for the initial board state
    function fenParser() {
        //parses the FEN string to variables
        const { fenBoard, fenTurn, fenCastling, fenEnPassant, fenHalfMoves, fenFullMoves } = ParseFEN(fen)

        const fenRows = fenBoard.split('/')

        let arr = [];

        //splits all rows and creates a matrix for the board
        for (let row of fenRows) {
            const tokens = row.split('')

            let r = []

            for (let i = 0; i < tokens.length; i++) {
                if (Number(tokens[i])) {
                    for (let j = 0; j < Number(tokens[i]); j++) {
                        r.push(' ')
                    }
                } else {
                    r.push(tokens[i])
                }
            }

            arr.push(r)
        }

        //generates a Square component for each element in the matrix created above
        let boardSquares = [];
        let squareColorIsWhite = true;

        for (let i = 0; i < arr.length; i++) {
            for (let j = 0; j < arr[i].length; j++) {
                let color = squareColorIsWhite ? "" : "dark";
                boardSquares.push(
                    // <Square
                    //     getPossibleMoves={getPossibleMoves}
                    //     key={`${i}-${j}`}
                    //     x={i}
                    //     y={j}
                    //     piece={ConvertFENPieceToPiece(arr[i][j])}
                    //     color={color}
                    //     selectedSquareX={selectedSquareX}
                    //     setSelectedSquareX={updateSelectedSquareX}
                    //     selectedSquareY={selectedSquareY}
                    //     setSelectedSquareY={updateSelectedSquareY}
                    //     possibleMoves={possibleMoves}
                    //     updatePossibleMoves={updatePossibleMoves}
                    //     movePiece={movePiece}
                    // />
                )
                squareColorIsWhite = !squareColorIsWhite;
            }
            squareColorIsWhite = !squareColorIsWhite;
        }

        let castles = fenCastling.split("");

        //parses the castling options from the FEN string
        for (let i = 0; i < castles.length; i++) {
            let castling = castles[i]

            switch (castling) {
                case "K":
                    setCanWhiteShortCastle(true)
                    break;
                case "k":
                    setCanBlackShortCastle(true)
                    break;
                case "Q":
                    setCanWhiteLongCastle(true)
                    break;
                case "q":
                    setCanBlackLongCastle(true)
                    break;
                default:
                    break;
            }
        }

        //updates the state
        setBoardPosition(arr)
        setNextTurn(fenTurn)
        setEnPassant(fenEnPassant)
        setHalfMoves(fenHalfMoves)
        setFullMoves(fenFullMoves)
        setBoardSquare(boardSquares)
        console.log('State updated')
    }

    function isWhiteInCheck(square, pseudo) {
        let squares = boardSquare

        if (pseudo) {
            const updatedSquares = squares.map(s => {
                if (s.x === pseudo.x && s.y === pseudo.y) {
                    // Return a new Square component with the updated piece
                    return React.cloneElement(s, { piece: pseudo.piece });
                } else {
                    // For other squares, return the original Square component
                    return s;
                }
            });

            squares = updatedSquares;
        }

        //check for attackers in front of the white king
        for (let i = square.x - 1; i >= 0; i--) {
            let piece = squares.find(s => s.x == i && s.y == square.y)
            if (piece.piece.split("-")[0] != "white") {
                if (piece.piece.split("-")[1] == "queen" || piece.piece.split("-")[1] == "rook") {
                    //console.log("in check from behind")
                    return true
                } else {
                    if (piece.piece) {
                        break
                    }
                }
            } else {
                break
            }
        }

        //check for attackers behind the white king
        for (let i = square.x + 1; i < DIM; i++) {
            let piece = squares.find(s => s.x == i && s.y == square.y)
            if (piece.piece.split("-")[0] != "white") {
                if (piece.piece.split("-")[1] == "queen" || piece.piece.split("-")[1] == "rook") {
                    //console.log("in check from the front")
                    return true
                } else {
                    if (piece.piece) {
                        break
                    }
                }
            } else {
                break
            }
        }

        //check for attackers to the left of the white king
        for (let i = square.y - 1; i >= 0; i--) {
            let piece = squares.find(s => s.x == square.x && s.y == i)
            if (piece.piece.split("-")[0] != "white") {
                if (piece.piece.split("-")[1] == "queen" || piece.piece.split("-")[1] == "rook") {
                    //console.log("in check from the left")
                    return true
                } else {
                    if (piece.piece) {
                        break
                    }
                }
            } else {
                break
            }
        }

        //check for attackers to the right of the white king
        for (let i = square.y + 1; i < DIM; i++) {
            let piece = squares.find(s => s.x == square.x && s.y == i)
            if (piece.piece.split("-")[0] != "white") {
