                    //console.log("in check from the right")
                    return true
                } else {
                    if (piece.piece) {
                        break
                    }
                }
            } else {
                break
            }
        }

        //check for attackers on a half of one of the diagonals (tlted counterclockwise)
        for (let i = 1; i < DIM; i++) {
            let piece = squares.find(s => s.x == square.x - i && s.y == square.y - i)

            if (piece) {
                if (piece.piece.split("-")[0] != "white") {
                    if (piece.piece.split("-")[1] == "queen" || piece.piece.split("-")[1] == "bishop" || piece.piece.split("-")[1] == "pawn") {
                        if (piece.piece.split("-")[1] == "pawn") {
                            if (piece.x - square.x == -1 && piece.y - square.y == -1) {
                                //console.log("in check from top half of counterclockwise diagonal by a pawn")
                                return true
                            } else {
                                break
