            }
        }

        //check for knights check
        let knight1 = squares.find(s => s.x == square.x - 2 && s.y == square.y - 1)
        let knight2 = squares.find(s => s.x == square.x - 2 && s.y == square.y + 1)
        let knight3 = squares.find(s => s.x == square.x + 2 && s.y == square.y - 1)
        let knight4 = squares.find(s => s.x == square.x + 2 && s.y == square.y + 1)
        let knight5 = squares.find(s => s.x == square.x - 1 && s.y == square.y + 2)
        let knight6 = squares.find(s => s.x == square.x - 1 && s.y == square.y - 2)
        let knight7 = squares.find(s => s.x == square.x + 1 && s.y == square.y + 2)
        let knight8 = squares.find(s => s.x == square.x + 1 && s.y == square.y - 2)

        if (knight1) {
            if (knight1.piece.split("-")[1] == "knight") {
                if (knight1.piece.split("-")[0] != "white") {
                    //console.log("in check by knight 1")
                    return true
                }
            }
        }

        if (knight2) {
            if (knight2.piece.split("-")[1] == "knight") {
                if (knight2.piece.split("-")[0] != "white") {
                    //console.log("in check by knight 2")
                    return true
                }
            }
        }

        if (knight3) {
            if (knight3.piece.split("-")[1] == "knight") {
                if (knight3.piece.split("-")[0] != "white") {
                    //console.log("in check by knight 3")
                    return true
                }
            }
        }

        if (knight4) {
            if (knight4.piece.split("-")[1] == "knight") {
                if (knight4.piece.split("-")[0] != "white") {
                    //console.log("in check by knight 4")
                    return true
                }
            }
        }

        if (knight5) {
            if (knight5.piece.split("-")[1] == "knight") {
                if (knight5.piece.split("-")[0] != "white") {
                    //console.log("in check by knight 5")
                    return true
                }
            }
        }

        if (knight6) {
            if (knight6.piece.split("-")[1] == "knight") {
                if (knight6.piece.split("-")[0] != "white") {
                    //console.log("in check by knight 6")
                    return true
