    function movePiece(x, y) {
        let squares = boardSquare;

        let targetSquare = getATargetSquareByLocation(x, y)
        let square = squares.find(s => s.x == selectedSquareX && s.y == selectedSquareY)

        let color = getPieceColor(square)
        let piece = getPiece(square)

        //disable castling if the king moves
        if (piece == "king") {
            if (isColorWhite(color)) {
                setCanWhiteLongCastle(false)
                setCanWhiteShortCastle(false)
            } else {
                setCanBlackLongCastle(false)
                setCanBlackShortCastle(false)
            }
        }

        //disable castling if the rook moves
        if (piece == "rook") {
            if (square.x == 7 && square.y == 7) {
                setCanWhiteShortCastle(false)
            }

            if (square.x == 0 && square.y == 7) {
                setCanBlackShortCastle(false)
            }

            if (square.x == 7 && square.y == 0) {
                setCanWhiteLongCastle(false)
            }

            if (square.x == 0 && square.y == 0) {
                setCanBlackLongCastle(false)
            }
        }

        let p = UpdatePosition(square, targetSquare, position)

        //capturing enPassant
        if (enPassantX && enPassantY && enPassantPossible) {
            let enPassantTargetSquare = color == "white" ? squares.find(s => s.x == x + 1 && s.y == y) : squares.find(s => s.x == x - 1 && s.y == y)
            let enPassantSquare = <Square x={square.x} y={square.y} piece={""} />

            p = UpdatePosition(enPassantSquare, enPassantTargetSquare, p)
        }

        //TODO: ensure p matches the new board Position format
        //setBoardPosition(p)
        setPossibleMoves([])
        setIsWhiteToMove(!isWhiteToMove)

        //enabling en passant
        if (piece == "pawn") {
            if (Math.abs(square.x - targetSquare.x) == 2) {
                let enPassant1 = squares.find(s => s.x == targetSquare.x && s.y == targetSquare.y - 1)
                let enPassant2 = squares.find(s => s.x == targetSquare.x && s.y == targetSquare.y + 1)

                if (enPassant1) {
                    if (enPassant1.piece.split("-")[1] == "pawn") {
                        if (enPassant1.piece.split("-")[0] != color) {
                            setEnPassantPossible(true)
                            setEnPassantX(targetSquare.x)
                            setEnPassantY(targetSquare.y)
                            return
                        }
                    }
                }

                if (enPassant2) {
                    if (enPassant2.piece.split("-")[1] == "pawn") {
                        if (enPassant2.piece.split("-")[0] != color) {
                            setEnPassantPossible(true)
                            setEnPassantX(targetSquare.x)
                            setEnPassantY(targetSquare.y)
                            return
                        }
                    }
                }
            }
        }

        setEnPassantPossible(false)
        setEnPassantX('')
        setEnPassantY('')
    }

    //updates the state with the possible moves
    function updatePossibleMoves(moves) {
        setPossibleMoves(moves)
    };

    //updates the state with the selectedSquare X coordinates
    function updateSelectedSquareX(x) {
        setSelectedSquareX(x)
    }

    //updates the state with the selectedSquare Y coordinates
    function updateSelectedSquareY(y) {
        setSelectedSquareY(y)
    }

    //retrieves a speecific square from the board using x and y coordinates (location)
    function getATargetSquareByLocation(x, y) {
        let squares = boardSquare

        return squares.find(s => s.x === x && s.y === y);
    }

    //retrieves a speecific square from the board that has a certain piece
    function getATargetSquareByPiece(piece) {
        let squares = boardSquare

        return squares.find(s => s.piece == piece);
    }

    //check if a specific square has a piece assigned to it
    function isSquareAvailable(square) {
        return square && !square.piece
    }

    //checks if a pawn is on its starting square and allows a 2-squares move
    function isPawnOnStartingSquare(pawn) {
        return isColorWhite(pawn.piece.split(PIECE_DELIMITER)[0]) ? pawn.x == 6 : pawn.x == 1
    }

    //checks if two pieces have the same color
    function areSameColor(square1, square2) {
        if (!square1 || !square2) {
            return
        }

        let color1 = square1.piece.split(PIECE_DELIMITER)[0];
        let color2 = square2.piece.split(PIECE_DELIMITER)[0];

        return color1 == color2
    }

    //checks if a square contains a certain piece
    function doesTheSquareHasThePiece(square, piece) {
        let p = getPiece(square)

        return p == piece
    }

    //cheks if a pawn is on En Passant square
    function isPawnOnEnPassantSquare(pawn) {
        return isColorWhite(pawn.piece.split(PIECE_DELIMITER)[0]) ? pawn.x == 3 : pawn.x == 4
    }

    //checks if a color is white
    function isColorWhite(color) {
        return color == WHITE
    }

    //checks if a color is black
    function isColorBlack(color) {
        return color == BLACK
    }

    //checks if white is allowed to long castle
    function checkIfCanWhiteLongCastle() {
        return canWhiteLongCastle &&
            boardPosition[7][0] == 'R' &&
            boardPosition[7][1] == '' &&
            boardPosition[7][2] == '' &&
            boardPosition[7][3] == '';
    }

    //checks if white is allowed to short castle
    function checkIfCanWhiteShortCastle() {
        return canWhiteShortCastle &&
            boardPosition[7][7] == 'R' &&
            boardPosition[7][6] == '' &&
            boardPosition[7][5] == '';
    }

    //checks if black is allowed to long castle
    function checkIfCanBlackLongCastle() {
        return canBlackLongCastle &&
            boardPosition[0][0] == 'r' &&
            boardPosition[0][1] == '' &&
            boardPosition[0][2] == '' &&
            boardPosition[0][3] == '';
    }

    //checks if black is allowed to short castle
    function checkIfCanBlackShortCastle() {
        return canBlackShortCastle &&
            boardPosition[7][7] == 'r' &&
            boardPosition[7][6] == '' &&
            boardPosition[7][5] == '';
    }

    //converts board coordinates to X and Y position on the board, e.g., a8 = 0, 0
    function convertCoordinatesToLocation(coordinates) {
        let tokens = coordinates.split('')

        //skip invalid coordinates
        if (tokens.length != 2) {
            return
        }

        let letter = tokens[0].toString().toUpperCase()
        let number = tokens[1]

        //65 is the ASCII code for capital A which will give us 0 if we have A as a coordinate; A-F will give us 0-7 for x
        //reverse the numbers since we draw the board top down
        return { x: letter.charCodeAt(0) - 65, y: DIM - Number(number) }
    }

    //gets all possible moves for a pawn
    function getPawnsPossibleMoves(square) {
        let color = getPieceColor(square) //get the piece color

        let moves = []

        let position1Offset = 1
        let position2Offset = 2

        //flips the direction if the color is white
        if (isColorWhite(color)) {
            position1Offset *= -1
            position2Offset *= -1
        }

        //get all forwards moves + the double move at the start
        let targetPiece1 = getATargetSquareByLocation(square.x + position1Offset, square.y) //move pawn 1 square ahead
        let targetPiece2 = getATargetSquareByLocation(square.x + position2Offset, square.y) //move pawn 2 squares ahead

        //get potential neighbors to capture
        let neighbor1 = getATargetSquareByLocation(square.x + position1Offset, square.y + position1Offset)
        let neighbor2 = getATargetSquareByLocation(square.x + position1Offset, square.y + (position1Offset * -1))

        //get the En Passant coordinates
        let enPassantCordinates = convertCoordinatesToLocation(enPassant)
        let enPassantSquare = enPassantCordinates ? getATargetSquareByLocation(enPassantCordinates.x, enPassantCordinates.y) : ''

        let isTheSquareInFrontAvailable = isSquareAvailable(targetPiece1)

        //push the one move forward for a pawn
        if (isTheSquareInFrontAvailable) {
            moves.push(targetPiece1)
        }

        //two moves are allowed only if the two squares above are free/empty
        //allowed if pawn hasn't moved before
        if (isTheSquareInFrontAvailable && isSquareAvailable(targetPiece2) && isPawnOnStartingSquare(square)) {
            moves.push(targetPiece2)
        }

        //push neighbour if there is a piece to capture and it's a different color than the pawn
        if (!isSquareAvailable(neighbor1) && !areSameColor(neighbor1, square)) {
            moves.push(neighbor1)
        }

        //push neighbour if there is a piece to capture and it's a different color than the pawn
        if (!isSquareAvailable(neighbor2) && !areSameColor(neighbor2, square)) {
            moves.push(neighbor2)
        }

        //add En Passant
        if (enPassantSquare) {
            moves.push(enPassantSquare)
        }

        return { piece: square, moves: moves }
    }

    //gets all possible moves for a rook
    function getRooksPossibleMoves(square) {
        let moves = []

        for (let i = square.x + 1; i < DIM; i++) {
            let targetSquare = getATargetSquareByLocation(i, square.y)

            if (isSquareAvailable(targetSquare)) {
                moves.push(targetSquare)
            } else {
                if (!areSameColor(square, targetSquare)) {
                    moves.push(targetSquare)
                }
                break
            }
        }

        for (let i = square.x - 1; i >= 0; i--) {
            let targetSquare = getATargetSquareByLocation(i, square.y)

            if (isSquareAvailable(targetSquare)) {
                moves.push(targetSquare)
            } else {
                if (!areSameColor(square, targetSquare)) {
                    moves.push(targetSquare)
                }
                break;
            }
        }

        for (let i = square.y + 1; i < DIM; i++) {
            let targetSquare = getATargetSquareByLocation(square.x, i)

            if (isSquareAvailable(targetSquare)) {
                moves.push(targetSquare)
            } else {
                if (!areSameColor(square, targetSquare)) {
                    moves.push(targetSquare)
                }
                break
            }
        }

        for (let i = square.y - 1; i >= 0; i--) {
            let targetSquare = getATargetSquareByLocation(square.x, i)

            if (isSquareAvailable(targetSquare)) {
                moves.push(targetSquare)
            } else {
                if (!areSameColor(square, targetSquare)) {
                    moves.push(targetSquare)
                }
                break
            }
        }

        return { piece: square, moves: moves }
    }

    //gets all possible moves for a bishop
    function getBishopPossibleMoves(square) {
        let moves = []

        let counter = 0;

        for (let i = 1; i <= DIM; i++) {
            if (counter == 5) {
                break;
            }

            let target1 = getATargetSquareByLocation(square.x - i, square.y - i)
            let target2 = getATargetSquareByLocation(square.x - i, square.y + i)
            let target3 = getATargetSquareByLocation(square.x + i, square.y + i)
            let target4 = getATargetSquareByLocation(square.x + i, square.y - i)

            if (target1 && isSquareAvailable(target1) && counter < 1) {
                moves.push(target1)
            } else {
                if (!areSameColor(square, target1)) {
                    moves.push(target1)
                }
                counter++;
            }

            if (target2 && isSquareAvailable(target2) && counter < 2) {
                moves.push(target2)
            } else {
                if (!areSameColor(square, target2)) {
                    moves.push(target2)
                }
                counter++;
            }

            if (target3 && isSquareAvailable(target3) && counter < 3) {
                moves.push(target3)
            } else {
                if (!areSameColor(square, target3)) {
                    moves.push(target3)
                }
                counter++;
            }

            if (target4 && isSquareAvailable(target4) && counter < 4) {
                moves.push(target4)
            } else {
                if (!areSameColor(square, target4)) {
                    moves.push(target4)
                }
                counter++;
            }
        }

        return { piece: square, moves: moves }
    }

    //gets all possible moves for a knight
    function getKnightPossibleMoves(square) {
        let offset = 1;
        let doubleOffset = 2 * offset;

        let moves = []

        for (let i = 0; i < 8; i++) {
            if (i != 0 && i % 2 == 0) {
                offset *= -1;
            }

            let targetX = square.x + offset;
            let targetY = square.y + doubleOffset;

            let target = getATargetSquareByLocation(targetX, targetY)

            if (target && isSquareAvailable(target)) {
                moves.push(target)
            } else {
                if (!areSameColor(square, target)) {
                    moves.push(target)
                }
            }

            doubleOffset *= -1

            if (i == 4) {
                offset *= 2
                doubleOffset /= 2
            }
        }

        return { piece: square, moves: moves }
    }

    //gets all possible moves for a king
    function getKingPossibleMoves(square) {
        let moves = []

        let color = getPieceColor(square)

        //gets all possible 1-square moves for the king
        for (let i = -1; i < 2; i++) {
            for (let j = -1; j < 2; j++) {
                let targetPiece = getATargetSquareByLocation(square.x + i, square.y + j)

                if (targetPiece && isSquareAvailable(targetPiece)) {
                    moves.push(targetPiece)
                } else {
                    if (!areSameColor(square, targetPiece)) {
                        moves.push(targetPiece)
                    }
                }
            }
        }

        //Castling logic
        const longCastleSquare = getATargetSquareByLocation(square.x, square.y - 2)
        const shortCastleSquare = getATargetSquareByLocation(square.x, square.y + 2)

        if (isColorWhite(color) && checkIfCanWhiteLongCastle()) {
            moves.push(longCastleSquare)
        }

        if (isColorWhite(color) && checkIfCanWhiteShortCastle()) {
            moves.push(shortCastleSquare)
        }

        if (isColorBlack(color) && checkIfCanBlackLongCastle()) {
            moves.push(longCastleSquare)
        }

        if (isColorBlack(color) && checkIfCanBlackShortCastle()) {
            moves.push(shortCastleSquare)
        }

        return { piece: square, moves: moves }
    }

    //gets all possible moves for a queen
    function getQueenPossibleMoves(square) {
        let moves = []

        //a queen has the same moves as a bishop and a rook
        const rookMoves = getRooksPossibleMoves(square).moves;
        const bishopMoves = getBishopPossibleMoves(square).moves;

        // Concatenate the arrays of moves
        moves.push(...rookMoves, ...bishopMoves);

        return { piece: square, moves: moves }
    }

    //gets the piece color
    function getPieceColor(square) {
        return square.piece.split(PIECE_DELIMITER)[0]
    }

    //gets the piece
    function getPiece(square) {
        return square.piece.split(PIECE_DELIMITER)[1]
    }

    //removes moves that do not deal with a check
    function filterMovesIfInCheck(possibleMoves) {
        let blackKing = getATargetSquareByPiece(BLACK_KING) //get the black king
        let whiteKing = getATargetSquareByPiece(WHITE_KING) //get the white king

        for (let i = 0; i < possibleMoves.length; i++) {
            for (let j = 0; j < possibleMoves[i].moves; j++) {
                if (possibleMoves[i].piece.piece == BLACK_KING) {
                    let move = <Square x={possibleMoves[i].moves[j].x} y={possibleMoves[i].moves[j].y} piece={"black-king"} />
                    let pseudo = <Square x={blackKing.x} y={blackKing.y} piece={""} />
                    let isInCheck = isBlackInCheck(move, pseudo)

                    if (isInCheck) {
                        possibleMoves[i].moves.splice(j, 1);
                        j--;
                    }
                } else if (possibleMoves[i].piece.piece == WHITE_KING) {
                    let move = <Square x={possibleMoves[i].moves[j].x} y={possibleMoves[i].moves[j].y} piece={"white-king"} />
                    let pseudo = <Square x={whiteKing.x} y={whiteKing.y} piece={""} />
                    let isInCheck = isWhiteInCheck(move, pseudo)

                    if (isInCheck) {
                        possibleMoves[i].moves.splice(j, 1);
                        j--;
                    }
                } else {
                    let move = <Square x={possibleMoves[i].moves[j].x} y={possibleMoves[i].moves[j].y} piece={possibleMoves[i].piece.piece} />
                    let isInCheck = isBlackInCheck(blackKing, move) ? true : isWhiteInCheck(whiteKing, move)

                    if (isInCheck) {
                        possibleMoves[i].moves.splice(j, 1);
                        j--;
                    }
                }
            }
        }

        return possibleMoves
    }

    //removes moves that allow illigal castling (castles through check for example)
    function filterMovesThatAllowIllegalCastling(possibleMoves) {
        let blackKing = getATargetSquareByPiece(BLACK_KING) //get the black king
        let whiteKing = getATargetSquareByPiece(WHITE_KING) //get the white king

        for (let i = 0; i < possibleMoves.length; i++) {
            let whiteCastle1 = possibleMoves[i].moves.find(m => m.x == 7 && m.y == 6) //sqaure where king will be after short castle (white)
            let whiteCastle2 = possibleMoves[i].moves.find(m => m.x == 7 && m.y == 2) //sqaure where king will be after long castle (white)
            let blackCastle1 = possibleMoves[i].moves.find(m => m.x == 0 && m.y == 6) //square where king will be after shot castle (black)
            let blackCastle2 = possibleMoves[i].moves.find(m => m.x == 0 && m.y == 2) //square where king will be after long castle (black)

            if (possibleMoves[i].piece == blackKing || possibleMoves[i].piece == whiteKing) {
                if (whiteCastle1) {
                    let pieceAcross = possibleMoves[i].moves.find(m => m.x == 7 && m.y == 5)
                    if (!pieceAcross) {
                        possibleMoves[i].moves.splice(possibleMoves[i].moves.indexOf(pieceAcross), 1)
                    }
                }

                if (whiteCastle2) {
                    let pieceAcross = possibleMoves[i].moves.find(m => m.x == 7 && m.y == 3)
                    if (!pieceAcross) {
                        possibleMoves[i].moves.splice(possibleMoves[i].moves.indexOf(pieceAcross), 1)
                    }
                }

                if (blackCastle1) {
                    let pieceAcross = possibleMoves[i].moves.find(m => m.x == 0 && m.y == 5)
                    if (!pieceAcross) {
                        possibleMoves[i].moves.splice(possibleMoves[i].moves.indexOf(pieceAcross), 1)
                    }
                }

                if (blackCastle2) {
                    let pieceAcross = possibleMoves[i].moves.find(m => m.x == 0 && m.y == 3)
                    if (!pieceAcross) {
                        possibleMoves[i].moves.splice(possibleMoves[i].moves.indexOf(pieceAcross), 1)
                    }
                }
            }

            //prevents black to castle if in check
            if (isBlackInCheck(blackKing)) {
                if (blackCastle1) {
                    possibleMoves[i].moves.splice(possibleMoves[i].moves.indexOf(castleMove), 1)
                }

                if (blackCastle2) {
                    possibleMoves[i].moves.splice(possibleMoves[i].moves.indexOf(castleMove2), 1)
                }
            }

            //prevents white to castle if in check
            if (isWhiteInCheck(whiteKing)) {
                if (whiteCastle1) {
                    possibleMoves[i].moves.splice(possibleMoves[i].moves.indexOf(castleMove), 1)
                }

                if (whiteCastle2) {
                    possibleMoves[i].moves.splice(possibleMoves[i].moves.indexOf(castleMove2), 1)
                }
            }
        }

        return possibleMoves
    }

    //removes empty/undefined values for generated moves
    function removeEmptyMoves(possibleMoves) {
        for (let i = 0; i < possibleMoves.length; i++) {
            if (possibleMoves[i].moves.length == 0) {
                possibleMoves.splice(i, 1)
                i--;
                continue;
            }

            for (let j = 0; j < possibleMoves[i].moves.length; j++) {
                if (!possibleMoves[i].moves[j]) {
                    possibleMoves[i].moves.splice(j, 1);
                    j--;
                }
            }
        }

        return possibleMoves
    }

    //gets all possible/legal moves
    function getPossibleMoves(x, y) {
        let square = getATargetSquareByLocation(x, y)
        // console.log(square)
        // console.log(fen, boardPosition, enPassant, boardSquare, possibleMoves,
        //     selectedSquareX, selectedSquareY, canWhiteShortCastle, canWhiteLongCastle,
        //     canBlackShortCastle, canBlackLongCastle, enPassantPossible, enPassantX, enPassantY, isWhiteToMove)
        console.log('Starting to get possible moves')
        let possiblePieceMoves = [];

        //if the square has a piece
        if (square.piece) {
            let color = getPieceColor(square); //get the piece color

            //don't collect possible moves if it's black to move and you're trying to move a white piece
            if (isColorWhite(color) && !isWhiteToMove) {
                return
            }

            //don't collect possible moves if it's white to move and you're trying to move a black piece
            if (isColorBlack(color) && isWhiteToMove) {
                return
            }

            //get all possible moves for each piece
            possiblePieceMoves.push(getPawnsPossibleMoves(square))
            possiblePieceMoves.push(getRooksPossibleMoves(square))
            possiblePieceMoves.push(getBishopPossibleMoves(square))
            possiblePieceMoves.push(getKnightPossibleMoves(square))
            possiblePieceMoves.push(getKingPossibleMoves(square))
            possiblePieceMoves.push(getQueenPossibleMoves(square))

            //filter empty moves
            possiblePieceMoves = removeEmptyMoves(possiblePieceMoves);

            //filter possible moves
            possiblePieceMoves = filterMovesIfInCheck(possiblePieceMoves)
            possiblePieceMoves = filterMovesThatAllowIllegalCastling(possiblePieceMoves)
        }

        console.log("Possible Moves:", possiblePieceMoves);
        updatePossibleMoves(possiblePieceMoves);
    }

    //renders the board
    return (
        <>
            <div className="chessGrid">
                {boardSquare.map((sqr) => sqr)}
            </div>
        </>
    );
}
