import React, { useState, useEffect, useRef } from "react";
import Square from "./Square.js"
import * as HelperMethods from "./HelperMethods.js";
import * as GlobalVariables from "./globalVariables.js"

function Board() {
    //<summary>
    //FEN - Forsyth-Edwards Notation
    //small letters - black pieces
    //capital letters - white pieces
    //numbers - empty board squares
    //(/) - separates each board row
    //first letter after the board indicates whose turn it is to move (w - white, b - black)
    //next letters indicate castling rights, k - king-side, q - queen-side, (-) - no castling allowed for any side
    //en passant square - when a pawn moves two squares, the square behind it is put in this place or (-) if no en passant square
    //the number of half turns (white moves, then black moves = 2 half moves) that were made without a pawn move or a capture of a piece (50-move rule when reaches 100)
    //the number of full moves (white moves, then black moves = 1 full move); increase after every black half move
    //</summary>
    //starting position
    //'rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 0'
    //examples of FEN positions
    //r1bqk2r/pppp1ppp/2n5/8/1bBPn3/2N2N2/PP3PPP/R1BQK2R w KQkq - 0 8
    //r1bqk2r/pppp1ppp/2n2n2/2b5/2BPP3/5N2/PP3PPP/RNBQK2R b KQkq - 0 6
    //r1b5/p1p3k1/2pp1r1p/6pB/3P4/8/P4PPP/Q1B3K1 b - - 0 21

    const [fen, setFen] = useState('r1b5/p1p3k1/2pp1r1p/6pB/3P4/8/P4PPP/Q1B3K1 b - - 0 21')
    const [isPossibleMove, setIsPossibleMove] = useState([])
    const [boardSquares, setBoardSquares] = useState([])
    const isPossibleMoveRef = useRef(isPossibleMove)
    const boardSquaresRef = useRef(boardSquares)

    //updates the possible move array
    //if a square is a possible move, it will receive a circle indicating you can move there
    const updateIsPossibleMove = (key, value) => {
        //we are updating the state based on the previous state to resolve the issue with pending state and queue in React
        setIsPossibleMove(prevState =>
            prevState.map(item =>
                //if the current item's key matches the key we're looking for
                //copy all properties of the object in a new object using the spread syntax (...)
                //modify the value property to whatever value we're using
                //copy the original item if no modifications are needed
                item.key === key ? { ...item, value: value } : item
            )
        );
    };

    //update the reference to the IsPossibleMove array whenever we set a new value to the array
    useEffect(() => {
        isPossibleMoveRef.current = isPossibleMove;
        fenParser(false);
    }, [isPossibleMove])

    //called when the page loads
    //removes all circles from squares indicating that they can be clicked to move a piece there
    //we parse the FEN to fill in the board
    useEffect(() => {
        resetPossibleMoves()
        fenParser()
    }, [])

    //parse the FEN and fill in the board with the new position when the FEN updates
    useEffect(() => {
        fenParser()
    }, [fen])

    //update the reference to the BoardSquares array whenever we set a new value to the array
    useEffect(() => {
        boardSquaresRef.current = boardSquares
    }, [boardSquares])

    /**
    * Resets possible moves highlighting.
    */
    function resetPossibleMoves() {
        const initialIsPossibleMove = [];
        for (let i = 0; i < DIM; i++) {
            for (let j = 0; j < DIM; j++) {
                initialIsPossibleMove.push({ key: `${i}-${j}`, value: false });
            }
        }
        setIsPossibleMove(initialIsPossibleMove);
    }

    /**
    
    Determines if a king is in check.
    
    @param {object} square - The king's square.
    
    @param {object} pseudo - The pseudo move to test.
    
    @param {object} [currentSquare=null] - The current square of the piece.
    
    @param {string} kingColor - The color of the king.
    
    @returns {boolean} - True if the king is in check, false otherwise.
    */
    function isKingInCheck(square, pseudo, currentSquare = null, kingColor) {
        let squares = boardSquaresRef.current;

        // Update the board state if a pseudo move is provided
        if (pseudo) {
            const updatedSquares = squares.map(s => {
                if (s.props.x === pseudo.props.x && s.props.y === pseudo.props.y) {
                    return React.cloneElement(s, { piece: pseudo.props.piece });
                } else if (currentSquare && s.props.x === currentSquare.props.x && s.props.y === currentSquare.props.y) {
                    return React.cloneElement(s, { piece: '' });
                } else {
                    return s;
                }
            });
            squares = updatedSquares;
        }

        // Helper function to check if a piece is an opponent's piece
        const isOpponentPiece = piece => piece && piece.props.piece.split('-')[0] !== kingColor;

        // Helper function to check for rook or queen in a given direction
        const checkLinearDirection = (dx, dy) => {
            let x = square.props.x + dx;
            let y = square.props.y + dy;
            while (x >= 0 && x < DIM && y >= 0 && y < DIM) {
                const piece = squares.find(s => s.props.x === x && s.props.y === y);
                if (isOpponentPiece(piece)) {
                    const pieceType = piece.props.piece.split('-')[1];
                    if (pieceType === 'queen' || pieceType === 'rook') {
                        return true;
                    } else if (pieceType) {
                        break;
                    }
                } else {
                    break;
                }
                x += dx;
                y += dy;
            }
            return false;
        };

        // Helper function to check for bishop or queen in a given diagonal direction
        const checkDiagonalDirection = (dx, dy) => {
            let x = square.props.x + dx;
            let y = square.props.y + dy;
            while (x >= 0 && x < DIM && y >= 0 && y < DIM) {
                const piece = squares.find(s => s.props.x === x && s.props.y === y);
                if (isOpponentPiece(piece)) {
                    const pieceType = piece.props.piece.split('-')[1];
                    if (pieceType === 'queen' || pieceType === 'bishop') {
                        return true;
                    } else if (pieceType === 'pawn') {
                        if ((dx === 1 || dx === -1) && ((kingColor === COLORS.WHITE && dy === -1) || (kingColor === COLORS.BLACK && dy === 1))) {
                            return true;
                        }
                        break;
                    } else if (pieceType) {
                        break;
                    }
                } else {
                    break;
                }
                x += dx;
                y += dy;
            }
            return false;
        };

        // Helper function to check for knights
        const checkKnight = (dx, dy) => {
            const x = square.props.x + dx;
            const y = square.props.y + dy;
            const piece = squares.find(s => s.props.x === x && s.props.y === y);
            return piece && isOpponentPiece(piece) && piece.props.piece.split('-')[1] === 'knight';
        };

        // Check for linear threats (rook, queen)
        if (checkLinearDirection(-1, 0) || checkLinearDirection(1, 0) || checkLinearDirection(0, -1) || checkLinearDirection(0, 1)) {
            return true;
        }

        // Check for diagonal threats (bishop, queen, pawn)
        if (checkDiagonalDirection(-1, -1) || checkDiagonalDirection(1, 1) || checkDiagonalDirection(-1, 1) || checkDiagonalDirection(1, -1)) {
            return true;
        }

        // Check for knight threats
        const knightMoves = [
            [-2, -1], [-2, 1], [2, -1], [2, 1],
            [-1, -2], [-1, 2], [1, -2], [1, 2]
        ];
        if (knightMoves.some(([dx, dy]) => checkKnight(dx, dy))) {
            return true;
        }

        return false;
    } function isBlackInCheck(square, pseudo, currentSquare = null) {
        let squares = boardSquaresRef.current

        if (pseudo) {
            const updatedSquares = squares.map(s => {
                if (s.props.x === pseudo.props.x && s.props.y === pseudo.props.y) {
                    // Return a new Square component with the updated piece
                    return React.cloneElement(s, { piece: pseudo.props.piece });
                } else if (currentSquare && s.props.x === currentSquare.props.x && s.props.y === currentSquare.props.y) {
                    return React.cloneElement(s, { piece: '' });
                }
                else {
                    // For other squares, return the original Square component
                    return s;
                }
            });

            squares = updatedSquares;
        }

        //check for attackers behind the black king
        for (let i = square.props.x - 1; i >= 0; i--) {
            let piece = squares.find(s => s.props.x == i && s.props.y == square.props.y)
            if (piece.props.piece.split("-")[0] != "black") {
                if (piece.props.piece.split("-")[1] == "queen" || piece.props.piece.split("-")[1] == "rook") {
