            }
        }

        //check for attackers on the other half of one of the diagonals (tlted counterclockwise)
        for (let i = 1; i < DIM; i++) {
            let piece = squares.find(s => s.props.x == square.props.x + i && s.props.y == square.props.y + i)

            if (piece) {
                if (piece.props.piece.split("-")[0] != "black") {
