    //parses the FEN for the initial board state
    fenParser() {
        //parses the FEN string to variables
        const { fenBoard, fenTurn, fenCastling, fenEnPassant, fenHalfMoves, fenFullMoves } = ParseFEN(this.state.fen)

        const fenRows = fenBoard.split('/')

        let arr = [];

        //splits all rows and creates a matrix for the board
        for (let row of fenRows) {
            const tokens = row.split('')

            let r = []

            for (let i = 0; i < tokens.length; i++) {
                if (Number(tokens[i])) {
                    for (let j = 0; j < Number(tokens[i]); j++) {
                        r.push(' ')
                    }
                } else {
                    r.push(tokens[i])
                }
            }

            arr.push(r)
        }

        //generates a Square component for each element in the matrix created above
        let boardSquares = [];
        let squareColorIsWhite = true;

        for (let i = 0; i < arr.length; i++) {
            for (let j = 0; j < arr[i].length; j++) {
                let color = squareColorIsWhite ? "" : "dark";
                boardSquares.push(<Square
                    getPossibleMoves={this.getPossibleMoves.bind(this)}
                    key={`${i}-${j}`}
                    x={i}
                    y={j}
                    piece={ConvertFENPieceToPiece(arr[i][j])}
                    color={color}
                    selectedSquareX={this.state.selectedSquareX}
                    setSelectedSquareX={this.updateSelectedSquareX}
                    selectedSquareY={this.state.selectedSquareY}
                    setSelectedSquareY={this.updateSelectedSquareY}
                    possibleMoves={this.state.possibleMoves}
                    updatePossibleMoves={this.updatePossibleMoves}
                    movePiece={this.movePiece}
                />)
                squareColorIsWhite = !squareColorIsWhite;
            }
            squareColorIsWhite = !squareColorIsWhite;
        }

        //collects the states to update
        let propsToUpdate = { boardPostition: arr, nextTurn: fenTurn, enPassant: fenEnPassant, halfMoves: fenHalfMoves, fullMoves: fenFullMoves, boardSquare: boardSquares }

        let castles = fenCastling.split("");

        //parses the castling options from the FEN string
        for (let i = 0; i < castles.length; i++) {
            let castling = castles[i]

            switch (castling) {
                case "K":
                    propsToUpdate.canWhiteShortCastle = true
                    break;
                case "k":
                    propsToUpdate.canBlackShortCastle = true
                    break;
                case "Q":
                    propsToUpdate.canWhiteLongCastle = true
                    break;
                case "q":
                    propsToUpdate.canBlackLongCastle = true
                    break;
                default:
                    break;
            }
        }

        //updates the state
        this.setState(propsToUpdate)
    }

