 * Checks if a character is Uppercase using the ASCII table; 65 (A) to 90 (Z)
 * @param {string} value - The character to check.
 * @returns {boolean} - True if the character is uppercase, false otherwise.
 */
export function IsUpperCase(value) {
    return value.charCodeAt(0) >= 65 && value.charCodeAt(0) <= 90;
}

/**
 * Checks if a character is Lowercase using the ASCII table; 97 (a) to 122 (z)
 * @param {string} value - The character to check.
 * @returns {boolean} - True if the character is lowercase, false otherwise.
 */
export function IsLowerCase(value) {
    return value.charCodeAt(0) >= 97 && value.charCodeAt(0) <= 122;
}

/**
 * Checks if a character is a Digit using the ASCII table; 48 (0) to 57 (9)
 * @param {string} value - The character to check.
 * @returns {boolean} - True if the character is a digit, false otherwise.
 */
export function IsDigit(value) {
    return value.charCodeAt(0) >= 48 && value.charCodeAt(0) <= 57;
}

/**
 * Splits the FEN string into its individual components.
 * @param {string} fen - The FEN string.
 * @returns {object} - An object containing the parts of the FEN string.
 * @throws {Error} - Throws an error if the FEN string is invalid.
 */
export function ParseFEN(fen) {
    const fenParts = fen.split(EMPTY_SQUARE_PIECE);
    if (fenParts.length !== 6) {
        throw new Error('Invalid FEN');
    }
    const [fenBoard, fenTurn, fenCastling, fenEnPassant, fenHalfMoves, fenFullMoves] = fenParts;
    return {
        fenBoard,
        fenTurn,
        fenCastling,
        fenEnPassant,
        fenHalfMoves: Number(fenHalfMoves),
        fenFullMoves: Number(fenFullMoves)
    };
}

/**
 * Converts FEN piece notation to an actual piece.
 * @param {string} letter - The FEN piece notation.
 * @returns {string} - The corresponding piece.
 */
export function ConvertFENtoPiece(letter) {
    const FEN_PIECE_MAP = {
        [FEN_PIECES_BLACK.PAWN]: PIECES.PAWN,
        [FEN_PIECES_BLACK.KNIGHT]: PIECES.KNIGHT,
        [FEN_PIECES_BLACK.KING]: PIECES.KING,
        [FEN_PIECES_BLACK.QUEEN]: PIECES.QUEEN,
        [FEN_PIECES_BLACK.ROOK]: PIECES.ROOK,
        [FEN_PIECES_BLACK.BISHOP]: PIECES.BISHOP,
    };

    return FEN_PIECE_MAP[letter.toLowerCase()] || "";
}

/**
 * Converts the FEN piece notation to a piece that we can use in the square component.
 * @param {string} value - The FEN piece notation.
 * @returns {string} - The piece with color information.
 */
export function ConvertFENPieceToPiece(value) {
    if (IsUpperCase(value)) {
        return `${COLORS.WHITE}-${ConvertFENtoPiece(value)}`;
    }

    if (IsLowerCase(value)) {
        return `${COLORS.BLACK}-${ConvertFENtoPiece(value)}`;
    }

    return "";
}

/**
 * Converts a string in FEN format to a normal string, e.g., 'p6p' => 'p      p'
 * @param {string} value - The FEN string.
 * @returns {string} - The converted string.
 */
export function ConvertFenToString(value) {
    return value.split('').reduce((result, char) => {
        if (IsDigit(char)) {
            return result + EMPTY_SQUARE_PIECE.repeat(Number(char));
        }
        return result + char;
    }, '');
}

/**
 * Checks if two squares are on the same row.
 * @param {object} square1 - The first square.
 * @param {object} square2 - The second square.
 * @returns {boolean} - True if both squares are on the same row, false otherwise.
 */
function checkIfTwoSquaresAreOnTheSameRow(square1, square2) {
    return square1.props.x == square2.props.x;
}

/**
 * Checks if the square is on the given row number.
 * @param {object} square - The square to check.
 * @param {number} rowNumber - The row number to check against.
 * @returns {boolean} - True if the square is on the given row, false otherwise.
 */
function isSquareOnRow(square, rowNumber) {
    return square.props.x == rowNumber;
}

/**
 * Gets the piece from a square.
 * @param {object} square - The square to get the piece from.
 * @returns {string} - The piece on the square.
 */
export function getPiece(square) {
    return square.props.piece.split(PIECE_DELIMITER)[PIECE_PIECE_INDEX];
}

/**
 * Checks if the given square has the specified piece.
 * @param {object} square - The square to check.
 * @param {string} piece - The piece to check for.
 * @returns {boolean} - True if the square has the specified piece, false otherwise.
 */
export function doesTheSquareHasThePiece(square, piece) {
    return getPiece(square) == piece;
}

/**
 * Gets the color of the piece on a square.
 * @param {object} square - The square to get the piece color from.
 * @returns {string} - The color of the piece.
 */
export function getPieceColor(square) {
    return square.props.piece.split(PIECE_DELIMITER)[PIECE_COLOR_INDEX];
}

/**
 * Checks if the given color is white.
 * @param {string} color - The color to check.
 * @returns {boolean} - True if the color is white, false otherwise.
 */
export function isColorWhite(color) {
    return color == COLORS.WHITE;
}

/**
 * Checks if the given color is black.
 * @param {string} color - The color to check.
 * @returns {boolean} - True if the color is black, false otherwise.
 */
export function isColorBlack(color) {
    return color == COLORS.BLACK
}

/**
 * Checks if two squares have a pice with the same color.
 * @param {object} square1 - The first square.
 * @param {object} square2 - The second square.
 * @returns {boolean} - True if the pieces color is the same, false otherwise.
 */
export function areSameColor(square1, square2) {
    if (!square1 || !square2) {
        return false
    }

    return getPieceColor(square1) == getPieceColor(square2)
}

/**
 * Checks if a square has a piece on it or if it's free to move to.
 * @param {object} square - The square to check.
 * @returns {boolean} - True if the square is free, false otherwise.
 */
export function isSquareAvailable(square) {
    return square && !square.props.piece
}

/**
 * Converts board coordinates to X and Y position on the board, e.g., a8 = 0, 0
 * @param {string} coordinates - The board coordinates to convert.
 * @returns {object} - Returns an object with x and y properties.
 */
export function convertCoordinatesToLocation(coordinates) {
    let tokens = coordinates.split('')

    //skip invalid coordinates
    if (tokens.length != 2) {
        return
    }

    let letter = tokens[0].toString().toUpperCase()
    let number = tokens[1]

    //65 is the ASCII code for capital A which will give us 0 if we have A as a coordinate; A-F will give us 0-7 for y (columns)
    //reverse the numbers since we draw the board top down for x (rows)
    return { x: DIM - Number(number), y: letter.charCodeAt(0) - 65 }
}

/**
 * Converts a piece location to board coordinate, e.g., 0, 0 to a8
 * @param {string} x - The X position.
 * @param {string} y - The Y position.
 * @returns {string} - Returns a string containing the board coordinates.
 */
export function convertLocationToCoordinates(x, y) {
    //maps y (column) to the letter cordinate; add 65 (capital A code) to the ASCII code for the location we have and we get a letter between A and F
    //reverses the x (rows) coordinatte becasue we drop the board top down, while coordinates go down up
    return `${String.fromCharCode(y + 65)}${DIM - x}`.toLowerCase()
}

/**
 * Determines if castling is possible.
 * @param {string} castlingType - The type of castling (long or short).
 * @param {boolean} [withWhite=true] - True if checking for white's castling, false otherwise.
 * @returns {boolean} - True if castling is possible, false otherwise.
 */
function areWeCastling(castlingType, withWhite = true) {
    const row = withWhite ? CASTLE_ROW_WHITE : CASTLE_ROW_BLACK;
    const pieces = withWhite ? FEN_PIECES_WHITE : FEN_PIECES_BLACK;
    const [rookCol, kingCol, pathCols] = castlingType === CASTLING_TYPES.LONG ?
        [CASTLE_ROOK_INITIAL_COL_LONG, CASTLE_KING_INITIAL_COL, CASTLE_PATH_COLS_LONG] :
        [CASTLE_ROOK_INITIAL_COL_SHORT, CASTLE_KING_INITIAL_COL, CASTLE_PATH_COLS_SHORT];

    return BoardPosition[row][rookCol] === pieces.ROOK &&
        BoardPosition[row][kingCol] === pieces.KING &&
        pathCols.every(col => BoardPosition[row][col] === EMPTY_SQUARE_PIECE);
}

/**
 * Updates a column on a row with a new value.
 * @param {array} row - The row to update.
 * @param {number} colIndex - The column index to update.
 * @param {string} newValue - The new value to set.
 * @returns {array} - The updated row.
 */
function updateColumnOnARow(row, colIndex, newValue) {
    row[colIndex] = newValue;
    return row;
}

/**
 * Removes the current square's piece and handles castling if applicable.
 * @param {object} square - The current square.
 * @param {object} targetSquare - The target square.
 * @param {array} row - The row to update.
 * @returns {array} - The updated row.
 */
function handleSquareOnCurrentRow(square, targetSquare, row) {
    return checkIfTwoSquaresAreOnTheSameRow(square, targetSquare) ?
        handleSquareAndTargetSquareOnTheSameRow(square, targetSquare, row) :
        updateColumnOnARow(row, square.props.y, EMPTY_SQUARE_PIECE);
}

/**
 * Updates the target square with the current square's piece.
 * @param {object} square - The current square.
 * @param {object} targetSquare - The target square.
 * @param {array} row - The row to update.
 * @returns {array} - The updated row.
 */
function handleTargetSquareOnCurrentRow(square, targetSquare, row) {
    return updateColumnOnARow(row, targetSquare.props.y, ConvertPieceToFENPiece(square.props.piece));
}

/**
 * Handles the scenario where the current square and target square are on the same row.
 * @param {object} square - The current square.
 * @param {object} targetSquare - The target square.
 * @param {array} row - The row to update.
 * @returns {array} - The updated row.
 */
function handleSquareAndTargetSquareOnTheSameRow(square, targetSquare, row) {
    if (doesTheSquareHasThePiece(square, PIECES.KING) && areWeCastling(square, targetSquare)) {
        return handleCastling(square, row);
    }
    return SidewaysCapture(row, Number(square.props.y), Number(targetSquare.props.y), square.props.piece);
}

/**
 * Performs castling, swapping the rook and the king.
 * @param {object} square - The current square.
 * @param {array} row - The row to update.
 * @returns {array} - The updated row.
 */
function handleCastling(square, row) {
    const color = getPieceColor(square);
    const isWhite = isColorWhite(color);
    const kingPiece = isWhite ? FEN_PIECES_WHITE.KING : FEN_PIECES_BLACK.KING;
    const rookPiece = isWhite ? FEN_PIECES_WHITE.ROOK : FEN_PIECES_BLACK.ROOK;

    Object.values(CASTLING_TYPES).forEach(type => {
        if (areWeCastling(type, isWhite)) {
            const [emptySquares, kingFinalCol, rookFinalCol] = type === CASTLING_TYPES.LONG ?
                [EMPTY_SQUARES_AFTER_LONG_CASTLE, CASTLE_KING_FINAL_COL_LONG, CASTLE_ROOK_FINAL_COL_LONG] :
                [EMPTY_SQUARES_AFTER_SHORT_CASTLE, CASTLE_KING_FINAL_COL_SHORT, CASTLE_ROOK_FINAL_COL_SHORT];
            emptySquares.forEach(col => row[col] = EMPTY_SQUARE_PIECE);
            row[kingFinalCol] = kingPiece;
            row[rookFinalCol] = rookPiece;
        }
    });

    return row;
}

/**
 * Updates the current position to a new position after making the move.
 * @param {object} square - The current square.
 * @param {object} targetSquare - The target square.
 * @param {array} position - The current position.
 * @returns {array} - The updated position.
 */
export function UpdatePosition(square, targetSquare, position) {
    return position.map((row, rowIndex) => {
        if (isSquareOnRow(square, rowIndex)) {
            row = handleSquareOnCurrentRow(square, targetSquare, row);
        }
        if (isSquareOnRow(targetSquare, rowIndex)) {
            row = handleTargetSquareOnCurrentRow(square, targetSquare, row);
        }
        return row;
    });
}

/**
 * Modifies the row to capture pieces sideways (square and target square are on the same row).
 * @param {array} row - The row to update.
 * @param {number} colIndex - The column index of the square.
 * @param {number} targetColIndex - The column index of the target square.
 * @param {string} value - The value to place at the target column index.
 * @returns {array} - The updated row.
 */
function SidewaysCapture(row, colIndex, targetColIndex, value) {
    return row.map((col, index) => {
        if (index === colIndex) return EMPTY_SQUARE_PIECE;
        if (index === targetColIndex) return ConvertPieceToFENPiece(value);
        return col;
    });
}

/**
 * Converts the piece from format {color-piece} to FEN format (a single letter).
 * @param {string} value - The piece in {color-piece} format.
 * @returns {string} - The piece in FEN format.
 */
function ConvertPieceToFENPiece(value) {
    const [color, piece] = value.split(PIECE_DELIMITER);
    const pieceMapping = {
        [PIECES.PAWN]: FEN_PIECES_BLACK.PAWN,
        [PIECES.KNIGHT]: FEN_PIECES_BLACK.KNIGHT,
        [PIECES.BISHOP]: FEN_PIECES_BLACK.BISHOP,
        [PIECES.ROOK]: FEN_PIECES_BLACK.ROOK,
        [PIECES.QUEEN]: FEN_PIECES_BLACK.QUEEN,
        [PIECES.KING]: FEN_PIECES_BLACK.KING,
    };

    const fenPiece = pieceMapping[piece];
    return isColorWhite(color) ? fenPiece.toUpperCase() : fenPiece;
}
