import React, { useState, useEffect } from 'react';

function Square({
    getPossibleMoves,
    x,
    y,
    piece,
    color,
    selectedSquareX,
    setSelectedSquareX,
    selectedSquareY,
    setSelectedSquareY,
    possibleMoves,
    updatePossibleMoves,
    movePiece
}) {

    function handleClick() {
        // console.log(`Square object`, possibleMoves.current)
        // if (possibleMoves.current.some(m => m.piece.props.x == x && m.piece.props.y == y)) {
        //     movePiece(x, y)
        // } else {
        //     if (selectedSquareX == x && selectedSquareY == y) {
        //         setSelectedSquareX("")
        //         setSelectedSquareY("")
        //         updatePossibleMoves([])
        //     } else {
        //         setSelectedSquareX(x)
        //         setSelectedSquareY(y)
        //         getPossibleMoves()
        //     }
        // }

        console.log(`Square object`, possibleMoves.current)
        const clickedSquare = possibleMoves.current.find(m => m.piece.props.x === x && m.piece.props.y === y);

        if (clickedSquare) {
            movePiece(x, y);
        } else {
            if (selectedSquareX === x && selectedSquareY === y) {
                setSelectedSquareX("");
                setSelectedSquareY("");
                updatePossibleMoves([]);
            } else {
                setSelectedSquareX(x);
                setSelectedSquareY(y);
                const square = { x, y, piece };
                const moves = getPossibleMovesForPiece(square); // new function to get possible moves for a specific piece
                updatePossibleMoves([{ piece: square, moves }]);
            }
        }
    }

    let styles = ['chessSquare', 'piece'];
    styles.push(color);
    styles.push(piece);

    if (possibleMoves.current.some(move => move.moves.some(m => m.props.x === x && m.props.y === y))) {
        styles.push("possibleMove");
    }

    return (
        <>
            <div onClick={handleClick} className={styles.filter(Boolean).join(" ")}>
                {/* <span className='coordinates'>{x}-{y}</span> */}
            </div>
        </>
    )
}

export default Square
